/*  this is based on Piper Haywoods's formula: https://piperhaywood.com/images-consistent-surface-area/ */
!function(){"use strict";window.onload=function(){document.querySelectorAll(".logos .logo").forEach(function(t){var o=Math.sqrt(5e3/(t.naturalWidth*t.naturalHeight));t.width=Math.round(t.naturalWidth*o),t.classList.remove("hidden")})}}();